// Example string: '\\"field1\\":\\"email1@gmail.com\\",\\"field1\\":\\"email2@gmail.com\\"'
let feedsIndex = databaseEmails.indexOf('\\"feeds\\"');
databaseEmails = databaseEmails.substring(feedsIndex);
// Define an empty array to hold the extracted emails
